import React, { useEffect, useState } from 'react';
import Timetable from './components/Timetable';
import AdminPanel from './components/AdminPanel';
import axios from 'axios';

function App() {
  const [teachers, setTeachers] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [selectedTeacher, setSelectedTeacher] = useState('');
  const [selectedRoom, setSelectedRoom] = useState('');
  const [schedules, setSchedules] = useState([]);
  const [viewMode, setViewMode] = useState('teacher'); // 'teacher' or 'room'
  const [refreshFlag, setRefreshFlag] = useState(false);

  useEffect(() => {
    axios.get('http://localhost:8000/teachers/')
      .then(res => setTeachers(res.data));
    axios.get('http://localhost:8000/rooms/')
      .then(res => setRooms(res.data));
  }, [refreshFlag]);

  useEffect(() => {
    if (viewMode === 'teacher' && selectedTeacher) {
      axios.get(`http://localhost:8000/teachers/${selectedTeacher}/schedules`)
        .then(res => setSchedules(res.data));
    } else if (viewMode === 'room' && selectedRoom) {
      axios.get(`http://localhost:8000/rooms/${selectedRoom}/schedules`)
        .then(res => setSchedules(res.data));
    } else {
      setSchedules([]);
    }
  }, [selectedTeacher, selectedRoom, viewMode, refreshFlag]);

  const handleRefresh = () => setRefreshFlag(f => !f);

  return (
    <div style={{ padding: 24 }}>
      <h1>학원 시간표/상담표</h1>
      <AdminPanel onRefresh={handleRefresh} />
      <div style={{ marginBottom: 16 }}>
        <button
          onClick={() => setViewMode('teacher')}
          style={{ marginRight: 8, fontWeight: viewMode === 'teacher' ? 'bold' : 'normal' }}
        >
          선생님별 보기
        </button>
        <button
          onClick={() => setViewMode('room')}
          style={{ fontWeight: viewMode === 'room' ? 'bold' : 'normal' }}
        >
          공간별 보기
        </button>
      </div>
      {viewMode === 'teacher' && (
        <select value={selectedTeacher} onChange={e => setSelectedTeacher(e.target.value)}>
          <option value="">선생님을 선택하세요</option>
          {teachers.map(teacher => (
            <option key={teacher.id} value={teacher.id}>{teacher.name}</option>
          ))}
        </select>
      )}
      {viewMode === 'room' && (
        <select value={selectedRoom} onChange={e => setSelectedRoom(e.target.value)}>
          <option value="">공간을 선택하세요</option>
          {rooms.map(room => (
            <option key={room.id} value={room.id}>{room.name}</option>
          ))}
        </select>
      )}
      <div style={{ marginTop: 32 }}>
        <Timetable schedules={schedules} />
      </div>
    </div>
  );
}

export default App;
