import React from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import { addDays, parse, set } from 'date-fns';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import { ko } from 'date-fns/locale';
import ScheduleModal from './ScheduleModal';
import axios from 'axios';

// 요일 한글 → 숫자 변환
const dayMap = {
  '월요일': 1,
  '화요일': 2,
  '수요일': 3,
  '목요일': 4,
  '금요일': 5,
  '토요일': 6,
  '일요일': 0,
};

function parseTime(timeStr) {
  // "13:30" → { hour: 13, minute: 30 }
  const [hour, minute] = timeStr.split(":").map(Number);
  return { hour, minute };
}

function makeEvents(schedules) {
  // 이번 주 월요일 기준
  const now = new Date();
  const monday = set(now, { hours: 0, minutes: 0, seconds: 0, milliseconds: 0 });
  monday.setDate(now.getDate() - ((now.getDay() + 6) % 7));

  return schedules.map(item => {
    const dayNum = dayMap[item.day_of_week];
    const start = new Date(monday);
    start.setDate(monday.getDate() + dayNum);
    const { hour: sh, minute: sm } = parseTime(item.start_time);
    start.setHours(sh, sm, 0, 0);
    const end = new Date(monday);
    end.setDate(monday.getDate() + dayNum);
    const { hour: eh, minute: em } = parseTime(item.end_time);
    end.setHours(eh, em, 0, 0);
    return {
      title: `${item.type} - ${item.student ? item.student.name : ''}`,
      start,
      end,
      resource: item,
      allDay: false,
    };
  });
}

const localizer = momentLocalizer(require('moment'));

function getMinMaxTime(events) {
  if (events.length === 0) {
    return {
      min: new Date(1970, 1, 1, 8, 0),
      max: new Date(1970, 1, 1, 23, 0),
    };
  }
  let minHour = 23, minMinute = 59, maxHour = 0, maxMinute = 0;
  events.forEach(ev => {
    const sh = ev.start.getHours();
    const sm = ev.start.getMinutes();
    const eh = ev.end.getHours();
    const em = ev.end.getMinutes();
    if (sh < minHour || (sh === minHour && sm < minMinute)) {
      minHour = sh; minMinute = sm;
    }
    if (eh > maxHour || (eh === maxHour && em > maxMinute)) {
      maxHour = eh; maxMinute = em;
    }
  });
  return {
    min: new Date(1970, 1, 1, minHour, minMinute),
    max: new Date(1970, 1, 1, maxHour, maxMinute),
  };
}

function Timetable({ schedules, onRefresh }) {
  const [modalOpen, setModalOpen] = React.useState(false);
  const [selectedEvent, setSelectedEvent] = React.useState(null);
  const events = makeEvents(schedules);
  const { min, max } = getMinMaxTime(events);

  // 일정 클릭시
  function handleSelectEvent(event) {
    setSelectedEvent(event);
    setModalOpen(true);
  }

  // 일정 삭제
  async function handleDelete() {
    if (!selectedEvent) return;
    await axios.delete(`http://localhost:8000/schedules/${selectedEvent.resource.id}`);
    setModalOpen(false);
    setSelectedEvent(null);
    if (onRefresh) onRefresh();
  }

  // 일정 수정
  async function handleUpdate(form) {
    if (!selectedEvent) return;
    await axios.patch(`http://localhost:8000/schedules/${selectedEvent.resource.id}`, form);
    setModalOpen(false);
    setSelectedEvent(null);
    if (onRefresh) onRefresh();
  }

  // 드래그&드롭, 리사이즈
  async function handleEventDropResize({ event, start, end }) {
    // 요일, 시간 계산
    const dayNames = ['일요일','월요일','화요일','수요일','목요일','금요일','토요일'];
    const day_of_week = dayNames[start.getDay()];
    const pad = n => String(n).padStart(2,'0');
    const start_time = pad(start.getHours()) + ':' + pad(start.getMinutes());
    const end_time = pad(end.getHours()) + ':' + pad(end.getMinutes());
    await axios.patch(`http://localhost:8000/schedules/${event.resource.id}`,
      { day_of_week, start_time, end_time });
    if (onRefresh) onRefresh();
  }

  return (
    <>
      <Calendar
        localizer={localizer}
        events={events}
        defaultView="week"
        views={["week"]}
        step={30}
        timeslots={1}
        style={{ height: 900 }}
        min={min}
        max={max}
        eventPropGetter={event => {
          let bg;
          if (event.resource.type === '상담') {
            bg = '#fff9c4'; // 연한 노란색
          } else if (event.resource.type === '수업') {
            bg = '#4e73df';
          } else {
            bg = '#e74a3b';
          }
          return { style: { backgroundColor: bg, color: 'black', borderRadius: 6 } };
        }}
        messages={{
          week: '주간',
          day: '일간',
          today: '오늘',
          previous: '이전',
          next: '다음',
        }}
        culture="ko"
        onSelectEvent={handleSelectEvent}
        draggableAccessor={() => true}
        resizable
        onEventDrop={args => handleEventDropResize(args)}
        onEventResize={args => handleEventDropResize(args)}
      />
      <ScheduleModal
        open={modalOpen}
        event={selectedEvent}
        onClose={() => setModalOpen(false)}
        onDelete={handleDelete}
        onUpdate={handleUpdate}
      />
    </>
  );
}

export default Timetable;
