import React, { useState, useMemo } from 'react';
import axios from 'axios';

const TABS = [
  { key: 'students', label: '학생' },
  { key: 'teachers', label: '선생님' },
  { key: 'rooms', label: '공간' },
  { key: 'schedules', label: '스케줄' },
];

function AdminPanel({ onRefresh }) {
  const [tab, setTab] = useState('students');
  const [teachers, setTeachers] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [students, setStudents] = useState([]);
  const [schedules, setSchedules] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // 데이터 불러오기
  const fetchAll = async () => {
    setLoading(true);
    try {
      const [t, r, s, sc] = await Promise.all([
        axios.get('http://localhost:8000/teachers/'),
        axios.get('http://localhost:8000/rooms/'),
        axios.get('http://localhost:8000/students/'),
        axios.get('http://localhost:8000/schedules/'),
      ]);
      setTeachers(t.data);
      setRooms(r.data);
      setStudents(s.data);
      setSchedules(sc.data);
      setError("");
    } catch (e) {
      setError(e.response?.data?.detail || '데이터 로딩 오류');
    }
    setLoading(false);
  };
  React.useEffect(() => { fetchAll(); }, []);

  // CRUD 핸들러 예시 (학생)
  const handleAddStudent = async (name) => {
    try {
      await axios.post('http://localhost:8000/students/', { name });
      fetchAll();
    } catch (e) {
      alert(e.response?.data?.detail || '학생 추가 오류');
    }
  };
  const handleEditStudent = async (id, name) => {
    try {
      await axios.patch(`http://localhost:8000/students/${id}`, { name });
      fetchAll();
    } catch (e) {
      alert(e.response?.data?.detail || '학생 수정 오류');
    }
  };
  const handleDeleteStudent = async (id) => {
    if (!window.confirm('정말 삭제할까요?')) return;
    try {
      await axios.delete(`http://localhost:8000/students/${id}`);
      fetchAll();
    } catch (e) {
      alert(e.response?.data?.detail || '학생 삭제 오류');
    }
  };
  // (선생님, 공간, 스케줄도 위와 유사하게 구현)

  // 학생 표
  function StudentTable() {
    const [editRow, setEditRow] = useState(null);
    const [editValue, setEditValue] = useState("");
    const [addValue, setAddValue] = useState("");
    return (
      <div>
        <table border="1" style={{width:'100%',marginBottom:12}}>
          <thead><tr><th>이름</th><th>수정</th><th>삭제</th></tr></thead>
          <tbody>
            {students.map(st => (
              <tr key={st.id}>
                <td>
                  {editRow === st.id ? (
                    <input value={editValue} onChange={e=>setEditValue(e.target.value)} onBlur={()=>setEditRow(null)} onKeyDown={e=>{
                      if(e.key==='Enter'){handleEditStudent(st.id,editValue);setEditRow(null);}
                    }} autoFocus />
                  ) : (
                    <span onClick={()=>{setEditRow(st.id);setEditValue(st.name);}} style={{cursor:'pointer'}}>{st.name}</span>
                  )}
                </td>
                <td><button onClick={()=>{setEditRow(st.id);setEditValue(st.name);}}>✎</button></td>
                <td><button onClick={()=>handleDeleteStudent(st.id)}>🗑</button></td>
              </tr>
            ))}
            <tr>
              <td><input value={addValue} onChange={e=>setAddValue(e.target.value)} placeholder="새 학생 이름" onKeyDown={e=>{if(e.key==='Enter'){handleAddStudent(addValue);setAddValue("");}}}/></td>
              <td colSpan={2}><button onClick={()=>{handleAddStudent(addValue);setAddValue("");}}>추가</button></td>
            </tr>
          </tbody>
        </table>
      </div>
    );
  }
  // (선생님, 공간, 스케줄 표도 유사하게 작성)

  // 탭 렌더링
  function renderTab() {
    if(tab==='students') return <StudentTable/>;
    // TODO: TeacherTable, RoomTable, ScheduleTable 구현
    return <div>준비 중...</div>;
  }

  return (
    <div style={{padding:24}}>
      <h2>데이터베이스 관리 (노션 스타일)</h2>
      <div style={{marginBottom:16}}>
        {TABS.map(t => (
          <button key={t.key} onClick={()=>setTab(t.key)} style={{fontWeight:tab===t.key?'bold':'normal',marginRight:8}}>{t.label}</button>
        ))}
      </div>
      {loading ? <div>로딩 중...</div> : error ? <div style={{color:'red'}}>{error}</div> : renderTab()}
    </div>
  );
}

export default AdminPanel;
